import React, { useState, useEffect } from "react";
import FirstSibling from "./FirstSibling";
import SecondSibling from "./SecondSibling";

function App() {
  const [counter, setCounter] = useState(0);
  const [start, setStart] = useState(false);
  const [stop, setStop] = useState(false);


  useEffect(() => {
   const timeout = setTimeout(() => {
      if (start) {
        startTimeout();
      }

    }, 1000);
    if(stop){
      return () => {clearTimeout(timeout)};
    }
  }, [counter, start , stop]);

  const startTimeout = () => {
    setCounter(counter + 1);
  };

  const checkStart = () => {
    setStart(true);
  };

  const stopHandler = () => {
    setStop(true);
  };

  return (
    <div>
      <h1>{counter}</h1>
      <button onClick={checkStart}>start counter</button>
      <FirstSibling stop={stopHandler}></FirstSibling>
      <SecondSibling counter={counter}></SecondSibling>
    </div>
  );
}

export default App;
