import React, { useState, useEffect } from "react";
import FirstSibling from "./FirstSibling";
import SecondSibling from "./SecondSibling";

function App() {
  const [counter, setCounter] = useState(0);
  let timeout;

 const start =  useEffect(() => {
    timeout = setTimeout(() => {
      setCounter(counter+1)
    }, 1000);
  },[counter]);

  const stopTimeout =() =>{
    return () =>{  clearTimeout(timeout)}
  }

  return (
    <div>
      <h1>{counter}</h1>
      <button onClick={start}>start counter</button>
      <FirstSibling stop={stopTimeout}></FirstSibling>
      <SecondSibling counterval={counter}></SecondSibling>
    </div>
  );
}

export default App;
