import React, { useState, useEffect } from "react";
import SecondSibling from "./SecondSibling";
import FirstSibling from "./FirstSibling";
import classes from "./index.module.css";

function App() {
  const [counter, setCounter] = useState(0);
  const [start, setStart] = useState(false);
  const [timeoutId, setTimeoutId] = useState(null);

  useEffect(() => {
    if (start) {
      const id = setTimeout(() => {
        setCounter((prevCounter) => prevCounter + 1);
      }, 1000);
      setTimeoutId(id);
    }
    return () => clearTimeout(timeoutId);
  }, [start]);

  const handleStart = () => {
    setStart(true);
  };

  const handleStop = () => {
    setStart(false);
    clearTimeout(timeoutId);
  };

  return (
    <div>
      <h1 className={classes.count}>{counter}</h1>

        <button className={classes.btn} onClick={handleStart}>
          start
        </button>


        <button className={classes.btn} onClick={handleStop}>
          stop
        </button>

      {/* <FirstSibling stop={handleStop}></FirstSibling> */}
      <SecondSibling counter={counter}></SecondSibling>
    </div>
  );
}

export default App;
