import React, { useState, useEffect } from "react";
import FirstSibling from "./FirstSibling";
import SecondSibling from "./SecondSibling";

function App() {
  const [counter, setCounter] = useState(0);
  const [start, setStart] = useState(false);
  const [timeoutId, setTimeoutId] = useState(null);

let timeout;
  useEffect(() => {
   timeout = setTimeout(() => {
      if (start) {
        startTimeout();
      }
    }, 1000);
  }, [counter, start]);
  setTimeoutId(timeout);

  const startTimeout = () => {
    setCounter(counter + 1);
  };

  const checkStart = () => {
    setStart(true);
  };

  function handleStopTimeout() {
    clearTimeout(timeoutId);
    setTimeoutId(null);
  }

  return (
    <div>
      <h1>{counter}</h1>
      <button onClick={checkStart}>start counter</button>
      <button onClick={handleStopTimeout}>stop</button>
      <SecondSibling counter={counter}></SecondSibling>
    </div>
  );
}

export default App;
