import React, { useState, useEffect } from "react";
import FirstSibling from "./FirstSibling";
import SecondSibling from "./SecondSibling";

function App() {
  const [counter, setCounter] = useState(0);
  const [start, setStart] = useState(false);
  let timeout;

  useEffect(() => {
    timeout = setTimeout(() => {
      if (start) {
        startTimeout();
      }
    }, 1000);

    const stopTimeout = () => {
      return () => {
        clearTimeout(timeout);
      };
    };
  }, [counter, start]);

  const startTimeout = () => {
    setCounter(counter + 1);
  };

  const checkStart = () => {
    setStart(true);
  };



  return (
    <div>
      <h1>{counter}</h1>
      <button onClick={checkStart}>start counter</button>
      <FirstSibling stop={stopTimeout}></FirstSibling>
      <SecondSibling counter={counter}></SecondSibling>
    </div>
  );
}

export default App;
