import React, { useState, useEffect } from "react";
import SecondSibling from "./SecondSibling";
import FirstSibling from "./FirstSibling";
import classes from './index.module.css'


function App() {
  const [counter, setCounter] = useState(0);
  const [start, setStart] = useState(false);
  const [stop, setStop] = useState(null);
  let val=start;

  let timeout;
  useEffect(() => {
    timeout = setTimeout(() => {
      if (start) {
        startTimeout();
      }
    }, 1000);
    setStop(timeout);
  }, [counter, start]);

  const startTimeout = () => {
    console.log(start)
    setCounter(counter + 1);
  };

  const checkStart = () => {
    console.log("yes start")
    setStart(true);
  };

  function handleStopTimeout() {
    clearTimeout(stop);
    setStop(timeout);
    console.log("yes" + start)
  }

  return (
    <div>
      <h1 className={classes.count}>{counter}</h1>
      <button className={classes.btn} onClick={checkStart}>start</button>
      <FirstSibling stop={handleStopTimeout}></FirstSibling>
      <SecondSibling counter={counter}></SecondSibling>
    </div>
  );
}

export default App;
